mapper.select:
SELECT 
FROM after_sale


        AND status = #{status}


        AND type = #{type}


        AND user_id = #{userId}


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}


ORDER BY create_time DESC

mapper.update:
UPDATE after_sale
status = #{status}, seller_response = #{sellerResponse}, arbitration_result = #{arbitrationResult}, arbitration_time = #{arbitrationTime}, complete_time = #{completeTime}, update_time = NOW()
WHERE id = #{id}

UPDATE after_sale SET status = #{status},
arbitration_result = #{arbitrationResult}, arbitration_time = NOW(),
update_time = NOW() WHERE id = #{id}

mapper.select:
SELECT a.*, o.order_no FROM after_sale a INNER JOIN `order` o ON a.order_id = o.id WHERE o.seller_id = #{sellerId}
AND a.status = #{status}
ORDER BY CASE WHEN a.status IN (0, 3) THEN 0 ELSE 1 END, a.create_time DESC
