mapper.select:
SELECT c.*, p.name as product_name FROM consult c INNER JOIN product p ON c.product_id = p.id WHERE p.user_id = #{merchantId}
AND c.status = #{status}
ORDER BY CASE WHEN c.status = 0 THEN 0 ELSE 1 END, c.create_time DESC

mapper.update:
UPDATE consult
status = #{status}, reply_content = #{replyContent}, reply_time = #{replyTime},
WHERE id = #{id}
