mapper.select:
SELECT 
FROM coupon
WHERE shop_id = #{shopId}

    AND status = #{status}

ORDER BY create_time DESC

mapper.update:
UPDATE coupon
name = #{name}, type = #{type}, condition_amount = #{conditionAmount}, discount_amount = #{discountAmount}, discount_rate = #{discountRate}, total_count = #{totalCount}, remaining_count = #{remainingCount}, per_limit = #{perLimit}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, update_time = NOW()
WHERE id = #{id}
