mapper.select:
SELECT COUNT(*)
FROM credit_appeal
WHERE user_id = #{userId}

mapper.update:
UPDATE credit_appeal
status = #{status}, feedback = #{feedback}, handler_id = #{handlerId}, handle_time = #{handleTime},
WHERE id = #{id}

mapper.select:
SELECT
DATE(create_time) as date,
SUM(change_score) as total_change,
AVG(after_score) as avg_score
FROM credit_record
WHERE user_id = #{userId}

    AND create_time >= #{startDate}


    AND create_time <= #{endDate}

GROUP BY DATE(create_time)
ORDER BY date DESC

SELECT type, COUNT(*) as count, SUM(change_score) as total_change FROM credit_record
AND create_time >= #{startDate}
AND create_time <= #{endDate}
GROUP BY type

SELECT 
FROM credit_record
WHERE business_id = #{businessId}

    AND type = #{type}
