mapper.select:
SELECT 
FROM dynamic
WHERE shop_id = #{shopId}
ORDER BY create_time DESC
LIMIT #{pageSize} OFFSET #{pageNum}

mapper.update:
UPDATE dynamic
title = #{title}, content = #{content}, images = #{images}, video_url = #{videoUrl}, product_id = #{productId}, status = #{status}, update_time = NOW()
WHERE id = #{id}
