mapper.select:
SELECT FROM user_equipment WHERE user_id = #{userId}
AND (name LIKE CONCAT('%', #{keyword}, '%') OR brand LIKE CONCAT('%', #{keyword}, '%') OR model LIKE CONCAT('%', #{keyword}, '%'))
AND category = #{category}
ORDER BY create_time DESC

mapper.update:
UPDATE user_equipment
name = #{name}, brand = #{brand}, model = #{model}, category = #{category}, purchase_date = #{purchaseDate}, usage_condition = #{usageCondition}, description = #{description}, images = #{images}, update_time = NOW()
WHERE id = #{id}
