mapper.select:
SELECT 
FROM exchange_order


        AND exchange_no LIKE CONCAT('%', #{exchangeNo}, '%')


        AND status = #{status}


        AND initiator_id = #{initiatorId}


        AND receiver_id = #{receiverId}


ORDER BY create_time DESC

mapper.update:
UPDATE exchange_order
status = #{status}, initiator_logistics_no = #{initiatorLogisticsNo}, receiver_logistics_no = #{receiverLogisticsNo}, initiator_ship_time = #{initiatorShipTime}, receiver_ship_time = #{receiverShipTime}, initiator_receive_time = #{initiatorReceiveTime}, receiver_receive_time = #{receiverReceiveTime}, pay_time = #{payTime}, complete_time = #{completeTime}, update_time = NOW()
WHERE id = #{id}
