mapper.insert:
INSERT INTO operation_log (
    operator_id, operator_name, operation, target_id, target_type,
    details, result, reason, ip_address, user_agent, create_time
) VALUES (
    #{operatorId}, #{operatorName}, #{operation}, #{targetId}, #{targetType},
    #{details}, #{result}, #{reason}, #{ipAddress}, #{userAgent}, #{createTime}
)

mapper.select:
SELECT 
FROM operation_log


        AND operator_id = #{operatorId}


        AND target_type = #{targetType}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}


ORDER BY create_time DESC
