mapper.select:
SELECT 
FROM operation_log


        AND operator_id = #{operatorId}


        AND operation = #{operation}


        AND target_type = #{targetType}


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}


ORDER BY create_time DESC

SELECT operation, COUNT(*) as count FROM operation_log
AND create_time >= #{startDate}
AND create_time <= #{endDate}
GROUP BY operation ORDER BY count DESC

SELECT operator_id, operator_name, COUNT(*) as count FROM operation_log
AND create_time >= #{startDate}
AND create_time <= #{endDate}
GROUP BY operator_id, operator_name ORDER BY count DESC

SELECT DATE(create_time) as date, COUNT(*) as count FROM operation_log
AND create_time >= #{startDate}
AND create_time <= #{endDate}
GROUP BY DATE(create_time) ORDER BY date DESC

SELECT COUNT(*) FROM operation_log


        AND operator_id = #{operatorId}


        AND operation = #{operation}


        AND target_type = #{targetType}


        AND create_time >= #{startDate}


        AND create_time <= #{endDate}
