mapper.select:
SELECT 
FROM `order`
WHERE id = #{id}

SELECT 
FROM `order`
WHERE order_no = #{orderNo}

SELECT COUNT(*)
FROM `order`
WHERE buyer_id = #{userId} OR seller_id = #{userId}

SELECT o.*, buyer.username as buyer_name, seller.username as seller_name FROM `order` o LEFT JOIN user buyer ON o.buyer_id = buyer.id LEFT JOIN user seller ON o.seller_id = seller.id
AND o.order_no LIKE CONCAT('%', #{orderNo}, '%')
AND o.order_type = #{orderType}
AND o.status = #{status}
AND buyer.username LIKE CONCAT('%', #{buyerName}, '%')
AND seller.username LIKE CONCAT('%', #{sellerName}, '%')
AND o.create_time >= #{startDate}
AND o.create_time <= #{endDate}
ORDER BY o.create_time DESC

mapper.update:
UPDATE `order`
status = #{status}, logistics_no = #{logisticsNo}, logistics_company = #{logisticsCompany}, seller_remark = #{sellerRemark}, close_reason = #{closeReason}, pay_time = #{payTime}, ship_time = #{shipTime}, receive_time = #{receiveTime}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM `order`
WHERE status = #{status}
AND create_time BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) FROM `order`
WHERE order_type = #{orderType}
AND create_time BETWEEN #{startDate} AND #{endDate}

SELECT COALESCE(SUM(total_amount), 0) FROM `order`
WHERE create_time BETWEEN #{startDate} AND #{endDate}
AND status = 3

SELECT COALESCE(SUM(total_amount), 0) FROM `order`
WHERE create_time BETWEEN #{startDate} AND #{endDate}
AND status = 3

SELECT COALESCE(SUM(total_amount), 0) FROM `order`
WHERE payment_method = #{paymentMethod}
AND create_time BETWEEN #{startDate} AND #{endDate}
AND status = 3

SELECT COALESCE(AVG(total_amount), 0) FROM `order`
WHERE create_time BETWEEN #{startDate} AND #{endDate}
AND status = 3

SELECT COALESCE(MAX(total_amount), 0) FROM `order`
WHERE create_time BETWEEN #{startDate} AND #{endDate}
AND status = 3

SELECT COUNT(*) FROM `order` o
INNER JOIN product p ON o.product_id = p.id
WHERE p.category = #{category}

SELECT o.*, buyer.username as buyer_name, buyer.phone as buyer_phone
FROM `order` o
LEFT JOIN user buyer ON o.buyer_id = buyer.id
WHERE o.seller_id = #{sellerId}

    AND o.order_no LIKE CONCAT('%', #{queryDTO.orderNo}, '%')


