mapper.select:
SELECT COUNT(*)
FROM orders
WHERE user_id = #{userId} OR other_user_id = #{userId}

mapper.insert:
INSERT INTO orders (
    order_no, order_type, user_id, other_user_id, product_id,
    product_name, product_image, product_count, amount, status,
    payment_method, create_time
) VALUES (
    #{orderNo}, #{orderType}, #{userId}, #{otherUserId}, #{productId},
    #{productName}, #{productImage}, #{productCount}, #{amount}, #{status},
    #{paymentMethod}, #{createTime}
)

mapper.update:
UPDATE orders
status = #{status}, payment_time = #{paymentTime}, delivery_time = #{deliveryTime}, complete_time = #{completeTime}, has_dispute = #{hasDispute},
WHERE id = #{id}
