mapper.select:
SELECT FROM post
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}
AND category = #{category}
ORDER BY is_top DESC, CASE WHEN status = 0 THEN 0 ELSE 1 END, create_time DESC

mapper.update:
UPDATE post
title = #{title}, content = #{content}, images = #{images}, category = #{category}, view_count = #{viewCount}, like_count = #{likeCount}, comment_count = #{commentCount}, is_essence = #{isEssence}, is_top = #{isTop}, status = #{status}, audit_remark = #{auditRemark}, audit_time = #{auditTime}, update_time = NOW()
WHERE id = #{id}

UPDATE post
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE post
SET like_count = like_count + 1
WHERE id = #{id}

UPDATE post
SET comment_count = comment_count + 1
WHERE id = #{id}
