mapper.select:
SELECT 
FROM report


        AND status = #{status}


        AND target_type = #{targetType}


        AND reporter_id = #{reporterId}


        AND reported_user_id = #{reportedUserId}


ORDER BY
CASE WHEN status = 0 THEN 0 ELSE 1 END,
create_time DESC

mapper.update:
UPDATE report
status = #{status}, handle_result = #{handleResult}, handle_time = #{handleTime}, handler_id = #{handlerId},
WHERE id = #{id}

UPDATE report
SET status = #{status},
    handle_result = #{handleResult},
    handle_time = NOW(),
    handler_id = #{handlerId}
WHERE id = #{id}
